mapper.select:
select count(*) 
from freeboard
where freeboard_writedate > DATE_ADD(now(), INTERVAL -1 month);

select sum(orderlist_cost) 
from orderlist 
where orderlist_date > DATE_ADD(now(), INTERVAL -1 month);

select count(*) 
from member
where member_join_date > DATE_ADD(now(), INTERVAL -1 month);
