mapper.select:
select * from lesson
where senior_no = #{senior_no}
and lesson_result = 2

mapper.insert:
select ifnull(max(lessondetail_chapter),0) from lessondetail where lesson_no = #{lesson_no}




insert into lessondetail(lesson_no, senior_no, lessondetail_title, lessondetail_content, lessondetail_chapter, member_id, lessondetail_thumb,lessondetail_running_time)
values(#{lesson_no},#{senior_no},#{lessondetail_title},#{lessondetail_content},#{lessondetail_chapter}+1,#{member_id}, #{lessondetail_thumb},#{lessondetail_running_time})

mapper.update:
update lessondetail
set lessondetail_title = #{lessondetail_title}, 
lessondetail_content = #{lessondetail_content}
where lessondetail_no = #{lessondetail_no}
